import { useEffect, useState } from 'react';
import { api } from 'helpers/api';
import { Spinner } from 'components/ui/Spinner';
import { Button } from 'components/ui/Button';
import { useHistory } from 'react-router-dom';
import BaseContainer from "components/ui/BaseContainer";
import "styles/views/Game.scss";

const Profile = () => {
  let userinfo = JSON.parse(localStorage.getItem('userinfo'))
  const history = useHistory();
  const [user, setUser] = useState(null);

  const back = () => {
    history.push('/platform');
  }
  const edit = () => {
    history.push('/profile-edit?userinfo=' + JSON.stringify(user));
  }

  const gotoCommunityRanking = () => {
    history.push('/communityranking')
  }

  const gotoGlobalRanking = () => {
    history.push('/globalranking')
  }

  let username = history.location.search
  username = username.slice(username.indexOf('=') + 1)
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await api.get('/user/' + username);
        await new Promise(resolve => setTimeout(resolve, 500));
        setUser(response.data);
      } catch (error) {
        history.push('/');
      }
    }
    fetchData();
  }, []);

  let content = <Spinner />;

  if (user) {
    content = (
      <div className="game">
        <ul className="game user-list" style={{color: 'white'}}>
          USERNAME: {user.username}
        </ul>
        <ul className="game user-list" style={{color: 'white'}}>
          EMAIL: {user.email}
        </ul>
        {(user&&user.id==userinfo.id)?<Button width="40%" onClick={() => edit()} style={{color: 'darkblue'}}>Edit</Button>:null}
        <Button width={(user&&user.id==userinfo.id)?'80%':'100%'} onClick={() => history.push('/resetpassword')} style={{ marginTop: "20px", color: 'darkblue'}}> RESET PASSWORD </Button>
        <div style={{ width: "300px", height: "2px", backgroundColor: "white", top: '50%', left: '50%', }}></div>
        <div style={{ width: "300px", height: "2px", backgroundColor: "white", top: '70%', left: '50%', }}></div>
      </div>
    );
  }

  let content2 = <Spinner />;

  if (user) {
    content2 = (
      <div className="game">
        <Button style={{ marginRight: '10px', backgroundColor: 'lightblue', border:'none', color: 'darkblue', fontWeight: 'bold'}}
          onClick={() => gotoCommunityRanking()}
        >
          COMMUNITY RANKING
        </Button>
        <Button style={{  backgroundColor: 'lightblue', border:'none', color: 'darkblue', fontWeight: 'bold'}}
          onClick={() => gotoGlobalRanking()}
        >
          GLOBAL RANKING
        </Button>
      </div>
    );
  }



  return (
    <div className="game">
      <div className="game container" style={{backgroundColor: 'blue', position: 'absolute', top: '35%', left: '30%', transform: 'translate(-50%, -50%)', width: '380px', height: '500px'}}>
        <div className="game-content" style={{position: 'absolute', top: '20%', left: '55%', transform: 'translate(-50%, -50%)'}}>
            {content}
        </div>
      </div>

      <div className="game container" style={{ backgroundColor: 'lightblue', position: 'absolute', top: '35%', left: '60%', transform: 'translate(-50%, -50%)' }}>
        <div className="button-container">
          {content2}
        </div>
      </div>

      <div className="button-container" style={{ position: 'absolute', top: '70%', left: '60%', transform: 'translate(-50%, -50%)' }}>
        <Button style={{ marginRight: '10px', backgroundColor: 'darkblue', color: 'white',fontWeight: 'bold', width: '100px' }}
          onClick={() => history.push(`/platform`)}
        >
          BACK
        </Button>
      </div>

    </div>
  );


}

export default Profile;
