import { useEffect, useState } from 'react';
import { api } from 'helpers/api';
import { Spinner } from 'components/ui/Spinner';
import { Button } from 'components/ui/Button';
import { useHistory } from 'react-router-dom';
import BaseContainer from "components/ui/BaseContainer";
import "styles/views/Game.scss";

const Profile = () => {
  let userinfo = JSON.parse(localStorage.getItem('userinfo'))
  const history = useHistory();
  const [user, setUser] = useState(null);

  const back = () => {
    history.push('/platform');
  }
  const edit = () => {
    history.push('/profile-edit?userinfo=' + JSON.stringify(user));
  }

  let username = history.location.search
  username = username.slice(username.indexOf('=') + 1)
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await api.get('/user/' + username);
        await new Promise(resolve => setTimeout(resolve, 500));
        setUser(response.data);
      } catch (error) {
        history.push('/');
      }
    }
    fetchData();
  }, []);

  let content = <Spinner />;

  if (user) {
    content = (
      <div className="game">
        <img src="https://images.app.goo.gl/vuYry2nXPQ1yvxR96" alt="Image description" />
        <ul className="game user-list">
          USERNAME: {user.username}
        </ul>
        <ul className="game user-list">
          EMAIL: {user.email}
        </ul>
        <Button width={(user&&user.id==userinfo.id)?'40%':'100%'} onClick={() => back()} > Back </Button>
        {(user&&user.id==userinfo.id)?<Button width="40%" onClick={() => edit()}>Edit</Button>:null}
      </div>
    );
  }

  return (
    <BaseContainer className="game container">
      <h2>User Profile{user?(' - ' + user.username):''}</h2>
      <p className="game paragraph">
      </p>
      {content}
    </BaseContainer>
  );
}

export default Profile;
