import {BrowserRouter, Redirect, Route, Switch} from "react-router-dom";
import {PlatformGuard} from "components/routing/routeProtectors/PlatformGuard";
import PlatformGuardRouter from "components/routing/routers/PlatformGuardRouter";
import {LoginGuard} from "components/routing/routeProtectors/LoginGuard";
import Login from "components/views/Login";
import {RegisterGuard} from "components/routing/routeProtectors/RegisterGuard";
import Register from "components/views/Register";
import {ResetPasswordGuard} from "components/routing/routeProtectors/ResetPasswordGuard";
import ResetPassword from "components/views/ResetPassword";

/**
 * Main router of your application.
 * In the following class, different routes are rendered. In our case, there is a Login Route with matches the path "/login"
 * and another Router that matches the route "/game".
 * The main difference between these two routes is the following:
 * /login renders another component without any sub-route
 * /game renders a Router that contains other sub-routes that render in turn other react components
 * Documentation about routing in React: https://reacttraining.com/react-router/web/guides/quick-start
 */
const AppRouter = () => {
  return (
    <BrowserRouter>
      <Switch>
        <Route path="/platform">
          <PlatformGuard>
            <PlatformRouter base="/platform"/>
          </PlatformGuard>
        </Route>
        <Route exact path="/login">
          <LoginGuard>
            <Login/>
          </LoginGuard>
        </Route>
        <Route exact path="/register">
          <RegisterGuard>
            <Register/>
          </RegisterGuard>
        </Route>
        <Route exact path="/resetpassword">
          <ResetPasswordGuard>
            <ResetPassword/>
          </ResetPasswordGuard>
        </Route>
        <Route exact path="/">
          <Redirect to="/register"/>
        </Route>
      </Switch>
    </BrowserRouter>
  );
};

/*
* Don't forget to export your component!
 */
export default AppRouter;
