import {BrowserRouter, Redirect, Route, Switch} from "react-router-dom";
import {UserlistGuard} from "components/routing/routeProtectors/UserlistGuard";
import UserlistRouter from "components/routing/routers/UserlistRouter";
import {ProfileGuard} from "components/routing/routeProtectors/ProfileGuard";
import {ProfileEditGuard} from "components/routing/routeProtectors/ProfileEditGuard";
import ProfileRouter from "components/routing/routers/ProfileRouter";
import Login from "components/views/Login";
import ProfileEdit from "components/views/ProfileEdit";
import {RegisterGuard} from "components/routing/routeProtectors/RegisterGuard";
import Register from "components/views/Register";

/**
 * Main router of your application.
 * In the following class, different routes are rendered. In our case, there is a Login Route with matches the path "/login"
 * and another Router that matches the route "/userlist".
 * The main difference between these two routes is the following:
 * /login renders another component without any sub-route
 * /userlist renders a Router that contains other sub-routes that render in turn other react components
 * Documentation about routing in React: https://reacttraining.com/react-router/web/guides/quick-start
 */
const AppRouter = () => {
  return (
    <BrowserRouter>
      <Switch>
      <Route path="/profile-edit">
        <ProfileEditGuard>
            <ProfileEdit base="/profile-edit"/>
          </ProfileEditGuard>
      </Route>
      <Route path="/login">
          <Login/>
      </Route>
      <Route path="/profile">
          <ProfileGuard>
            <ProfileRouter base="/profile"/>
          </ProfileGuard>
        </Route>
        <Route path="/platform">
          <PlatformGuard>
            <PlatformRouter base="/platform"/>
          </PlatformGuard>
        </Route>
        <Route exact path="/register">
          <RegisterGuard>
            <Register/>
          </RegisterGuard>
        </Route>
        <Route exact path="/">
          <Redirect to="/register"/>
        </Route>
      </Switch>
    </BrowserRouter>
  );
};

/*
* Don't forget to export your component!
 */
export default AppRouter;
