import { useEffect, useState } from 'react';
import { api } from 'helpers/api';
import { Spinner } from 'components/ui/Spinner';
import { Button } from 'components/ui/Button';
import { useHistory } from 'react-router-dom';
import BaseContainer from "components/ui/BaseContainer";
import "styles/views/Game.scss";

const ProfileEdit = () => {
  const history = useHistory();

  const back = () => {
    history.push('/platform');
  }

  let userObj = null;
  let userinfo = history.location.search
  if (userinfo) {
    userObj = JSON.parse(decodeURI(userinfo.slice(userinfo.indexOf('=') + 1)))
  }

  const [user, setUser] = useState(userObj);

  const [username, susername] = useState(user.username);
  const [birthDay, sbirthDay] = useState(user.birthday);

  const doEdit = async () => {
    try {
      user.username = username
      user.birthday = birthDay?new Date(birthDay).toUTCString():null
      const requestBody = JSON.stringify(user);
      const response = await api.post('/user', requestBody);
      if(response.data.code == 200){
        history.push(`/profile?username=${username}`)
      }else{
        alert(response.data.msg);
      }
    } catch (error) {
      alert(`edit error`);
    }
  };

  let content = <Spinner />;

  if (user) {
    content = (
      <div className="game">
        <div>
          <label>
            Username:
          </label>
          <div>
            <input
              className="login input"
              placeholder="enter here..."
              value={username}
              type={'text'}
              onChange={e => susername(e.target.value)}
            />
          </div>
        </div>
        <div>
          <label>
            Birth date:
          </label>
          <div>
            <input
              className="login input"
              placeholder="in the format yyyy-mm-dd..."
              value={birthDay}
              type={'text'}
              onChange={e => sbirthDay(e.target.value)}
            />
          </div>
        </div>
        <Button width={'40%'} onClick={() => doEdit()} > Submit </Button>
        <Button width={'40%'} onClick={() => back()} > Back </Button>
      </div>
    );
  }

  return (
    <BaseContainer className="game container" style={{width: '400px'}}>
      <h2>Edit {user ? (' - ' + user.username) : ''}</h2>
      <p className="game paragraph">
      </p>
      {content}
    </BaseContainer>
  );
}

export default ProfileEdit;
